final _currencyNames = [
  {
    "id": 39,
    "name": "Canada",
    "isoAlpha2": "CA",
    "isoAlpha3": "CAN",
    "isoNumeric": 124,
    "currency": {"code": "CAD", "name": "Dollar", "symbol": "\$"},
    "flag":
        ""
  },
  {
    "id": 227,
    "name": "United States",
    "isoAlpha2": "US",
    "isoAlpha3": "USA",
    "isoNumeric": 840,
    "currency": {"code": "USD", "name": "Dollar", "symbol": "\$"},
    "flag":
        ""
  },
];

Map<String, dynamic>? getCurrencyDetailsByCountryName(String? countryName) {
  if (countryName == null) {
    return null;
  }

  final index = _currencyNames.indexWhere((element) =>
      element['name'].toString().toLowerCase() == countryName.toLowerCase());
  if (index != -1) {
    return _currencyNames[index]['currency'] as Map<String, dynamic>;
  } else {
    return _currencyNames.firstWhere(
            (element) => element['name'] == 'Canada')['currency']
        as Map<String, dynamic>;
  }
}
